<?php// add sub-menu support int bootstraprequire_once('wp-bootstrap-navwalker.php');// add thembnail img support for the themeadd_theme_support('post-thumbnails');//function to add Style filesfunction xlion_add_styles(){	wp_enqueue_style('bootstrap-css', get_template_directory_uri().'/css/bootstrap.min.css');	wp_enqueue_style('fontawesome-css', get_template_directory_uri().'/css/font-awesome.min.css');	wp_enqueue_style('main-css', get_template_directory_uri().'/css/main.css');}// function to add scripts js filefunction xlion_add_scripts(){	wp_enqueue_script('bootstrap-js', get_template_directory_uri(). '/js/bootstrap.min.js', array('jquery'), false, true);	wp_enqueue_script('main-js', get_template_directory_uri(). '/js/main.js', array(), false, true);	wp_enqueue_script('html5shiv', get_template_directory_uri(). '/js/html5shiv.js');	wp_script_add_data('html5shiv','conditional','lt IE 9');	wp_enqueue_script('respond', get_template_directory_uri(). '/js/respond.min.js');	wp_script_add_data('respond','conditional','lt IE 9');}// Add Actionsadd_action('wp_enqueue_scripts', 'xlion_add_styles');add_action('wp_enqueue_scripts', 'xlion_add_scripts');// Add Menu (navBar)function xlion_register_custom_menu(){	register_nav_menus(array(			'bootstrap-menu'	=> 'Navigate Bar',			'footer-menu'		=> 'Footer Menu'		));}function xlion_bootstrap_menu(){	wp_nav_menu(array(			'theme_location'	=> 'bootstrap-menu',			'menu_class'		=> 'nav navbar-nav navbar-right',			'container'			=> false,			'depth'				=> 2,			'walker'			=> new WP_Bootstrap_Navwalker()		));}add_action('init','xlion_register_custom_menu');//add_action('init','xlion_bootstrap_menu');// function to control the excerpt lenghtfunction xlion_extend_excerpt_length(){	return 15;}add_filter('excerpt_length','xlion_extend_excerpt_length');// change the dots stylefunction xlion_excerpt_change_dots($more){	return ' .^^.';}add_filter('excerpt_more','xlion_excerpt_change_dots');?>